% FUNCTION TO WRITE MINSEQS TO FILEfunction write_minseq_to_file_v1(dir_nm,pathx,max_cut_i,seq_to_print)if exist([dir_nm '/' pathx '/resf.mat'])    load (['./' dir_nm '/' pathx '/resf.mat'],'seqsx_inx_c_arr','int_nu2x_c_arr2','min_half_nmer',...        'max_gap','l','max_half_nmer','pow_factor');    seq_ix_t=seqsx_inx_c_arr{max_cut_i};    seqx=get_index_from_seqs_withN_gp_v1(seq_ix_t,l,max_half_nmer);    seq_to_print2=min(seq_to_print,length(seqx));    all_len_nox_c=zeros(seq_to_print2,1);    int_t=int_nu2x_c_arr2{max_cut_i};    fid=fopen(['./' dir_nm '/MinSeqs/' pathx '_' num2str(max_cut_i) '_' num2str(seq_to_print2) '.txt'],'w');    for j=1:seq_to_print2        all_len_nox_c(j)=length(find(seqx{j}~='N'));            fprintf(fid,'%s\t%s\t%f\t%d\t%f\n',seqx{j},seqrcomplement_db(seqx{j}),int_t(j),all_len_nox_c(j),...                int_t(j)*(pow_factor^all_len_nox_c(j)));    end    fclose(fid);end